import { useEffect, useState } from "react";
import { useLoaderData } from "react-router-dom";
import { LoadedArticle } from "./BlogPost";

const RSS = require("rss");

export const articleLoader = async (): Promise<any> => {
    const res = await fetch("https://raw.githubusercontent.com/GurkNathe/Personal/main/articles/articles.json");
    const articles = res.json();
    return articles;
};

export default function RSSFeed() {
    const data = useLoaderData() as LoadedArticle[];
    const [xml, setXML] = useState<string>(""); // Needs typing

    useEffect(() => {
        let feed = new RSS({
            title: "Ethan's Blog",
            description: "A catalogue of my various articles.",
            site_url: "https://gurknathe.github.io/Personal/#/rss.xml",
            copyright: "2023 Ethan Krug",
            language: "en"
        });

        data.forEach((item) => {
            feed.item({
                title:  item.title,
                description: item.summary,
                url: `https://gurknathe.github.io/Personal/#/blog/${item.contentUrl}/article`,
                categories: item.tags,
                author: 'Ethan Krug',
                date: item.timestamp
            });
        })

        setXML(feed.xml());
    }, []);

    return(<div dangerouslySetInnerHTML={{ __html: xml }}/>) // error: Type 'undefined' is not assignable to type 'string | TrustedHTML'.ts(2322)
}
