export interface RSSOptions {
    copyright?: string;
    description: string;
    language?: string;
    title: string;
    url: string;
}

export interface RSSItem {
    title:  string;
    description: string;
    url: string;
    guid: string;
    categories?: string[];
    author?: string;
    date?: Date;
}

export default class RSS {
    options: RSSOptions;
    items: RSSItem[];

    constructor(options: RSSOptions) {
        this.options = options;
        this.items = [];
    }

    addItem(item: RSSItem) : void {
        this.items.push(item);
    }

    createXML() : string {
        return "";
    }
}
